import React, { useEffect } from "react";
import { useState } from "react";
import { Link } from "react-router-dom";
import axios from 'axios'

export function Users(){
    const [users, setUsers] = useState([])

    useEffect(() => {
        axios.get('http://localhost:3001')
        .then(result => setUsers(result.data))
        .catch(err => console.log(err))
    }, [])
    const handleDelete = (id) => {
        axios.delete("http://localhost:3001/deleteUser"+ id)
        .then(result => console)
    }
    return(
        <>
        <div className="d-flex vh-100 bg-light justify-content-center align-items-center">
            <div className="w-50 bg-secondary rounded p-3">
                <Link to='/create' className="btn btn-success m-2">Add +</Link>
                <table className="table">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Phone</th>
                            <th>Age</th>
                            <th>Location</th>
                            <th>Action</th> 
                        </tr>
                    </thead>
                    <tbody>
                        {
                            users.map((user) => {
                               return <tr>
                                    <td>{user.name}</td>
                                    <td>{user.email}</td>
                                    <td>{user.phone}</td>
                                    <td>{user.age}</td>
                                    <td>{user.location}</td>
                                    <td>
                                    <Link to={`/update/${user._id}`}><button className="btn btn-warning">Update</button></Link>
                                        <button className="btn btn-danger m-2" onClick={(e) => handleDelete(user._id)}>Delete</button>
                                    </td>
                                </tr>
                            })
                        }
                    </tbody>
                </table>
            </div>
        </div>
        </>
    );
}
