import React, { useEffect } from "react";
import { useState } from "react";
import { Link } from "react-router-dom";

export function Users(){
    const [users, setUsers] = useState([{
        Name:"Kavin",Email:"kavin123@gmail.com",Phone:"9629187123",Location:"Coimbatore"
    }])

    useEffect(() => {
        axios.get('/')
    }, [])
    return(
        <>
        <div className="d-flex vh-100 bg-light justify-content-center align-items-center">
            <div className="w-50 bg-secondary rounded p-3">
                <Link to='/create' className="btn btn-success m-2">Add +</Link>
                <table className="table">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Phone</th>
                            <th>Age</th>
                            <th>Location</th>
                            <th>Action</th> 
                        </tr>
                    </thead>
                    <tbody>
                        {
                            users.map((user) => {
                                return <tr>
                                    <td>{user.Name}</td>
                                    <td>{user.Email}</td>
                                    <td>{user.Phone}</td>
                                    <td>{user.Age}</td>
                                    <td>{user.Location}</td>
                                    <td>
                                    <Link to='/update' className="btn btn-warning m-2">Update</Link>
                                        <button>Delete</button>
                                    </td>
                                </tr>
                            })
                        }
                    </tbody>
                </table>
            </div>
        </div>
        </>
    );
}
